const React = require('react');
const ReactDOMServer = require('react-dom/server');
function Hello(props){ return React.createElement('div', null, `Hello ${props.name}!`);}
const element = React.createElement(Hello, {name: 'React 16'});
const html = ReactDOMServer.renderToString(element);
console.log(html);

solution:
const assert = require('assert');
assert.strictEqual(typeof html, 'string');
assert.ok(html.includes('Hello React 16'));

