import cats.implicits._
object CatsCase {
  def combine(a: Option[Int], b: Option[Int]): Option[Int] = (a, b).mapN(_ + _)
}

object ColorCase {
  enum Color { case Red, Blue, Green }
  def isPrimary(c: Color): Boolean = c match {
    case Color.Red | Color.Blue => true
    case _ => false
  }
}
